import React, { Component } from 'react'

export default class BookComponent extends Component {
  render() {
    var style = {
        border: "1ps solid",
        backgroundColor:"#006600",
        borderRadius: '5px',
        color: 'white',
        width: '300px',
        margin: '40px auto',
        textAlign: 'center'
    };
    
    return (
      <div style={style}>
        <h2>Book Name : {this.propsprams.bookName}</h2>
        <h2>Writer Name :</h2>
        <input type="text" />
        <br/>
        <br/>
      </div>
    )
  }
}
